import React, { useState, useEffect } from "react";

const GoogleAuth = () => {
  const [isSignedIn, setisSignedIn] = useState(null);
  useEffect(() => {
    // GAPI is Google's client library for browser-side JavaScript. It's used in Google Sign-in, Google Drive
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "753149280389-6o5bi8ko4u8be2ohmehgcfin93jn4agj.apps.googleusercontent.com",
          // scope=user info taken by our app
          scope: "email",
        })
        .then(() => {
          const auth = window.gapi.auth2.getAuthInstance();
          setisSignedIn(auth.isSignedIn.get());
        });
    });
  }, []);
  const renderAuthButton = () => {
    if (isSignedIn === null) {
      return <div>Dont know if we are signed in</div>;
    } else if (isSignedIn) {
      return <div>I am signed in</div>;
    } else {
      return <div>I am not signed in</div>;
    }
  };
  return <div>{renderAuthButton}</div>;
};

export default GoogleAuth;
